import React, {Component} from 'react';
import mapboxgl from 'mapbox-gl';

//https://blog.mapbox.com/mapbox-gl-js-react-764da6cc074a

mapboxgl.accessToken = 'pk.eyJ1IjoiY2Rmcml0ejciLCJhIjoiY2sydzBmenM3MGF1djNrcWR3YzRmaGQ0aCJ9.y0TZwO3PN1sYjakT02t1fQ';

class MapBoxMapComponent extends Component {

  constructor(props){
    super(props);
    this.state = {
      lng: 5,
      lat: 34,
      zoom: 10
    }
  }

  componentDidMount() {
    const {lng, lat, zoom} = this.state;

    const map = new mapboxgl.Map({
      container: this.mapContainer,
      style: 'mapbox://styles/mapbox/streets-v9',
      center: [lng, lat],
      zoom
    });

    map.on('move', ()=> {
      const {lng, lat} = map.getCenter();

      this.setState({
        lng: lng.toFixed(4),
        lat: lat.toFixed(4),
        zoom: mat.getZoom().toFixed(2)
      });
    });
  }

  componentWillUnmount(){
    this.map.remove();
  }

  render() {

    const {lng, lat, zoom} = this.state;

    const style = {
      position: 'absolute',
      top: 0,
      bottom: 0,
      width: '100%'
    };

    return <div style={style} ref={el => this.mapContainer = el} />;
  }
}

export default MapBoxMapComponent;
