import React, {Component} from 'react';
import {Form,
        Container,
        Row,
        Col,
        Card,
        Button} from 'react-bootstrap';

class EventPage extends Component {

  constructor(props){
    super(props);

    this.state = {
      name:'',
      latitude:0.0,
      longitude:0.0,
      description:''
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleNameChange = this.handleNameChange.bind(this);
    this.handleLatitudeChange = this.handleLatitudeChange.bind(this);
    this.handleLongitudeChange = this.handleLongitudeChange.bind(this);
    this.handleDescChange = this.handleDescChange.bind(this);

  }

  handleSubmit(event) {
    event.preventDefault();
    console.log('Submitted');

    //convert to latitude longitude
    var latlng = this.state.search.split(" ");
    var lat = latlng[0];
    var lng = latlng[1];

    this.setState({toMapPage:true, latitude:lat, longitude:lng});
  }

  handleNameChange(event){
    this.setState({name:event.target.value});
  }

  handleDescChange(event){
    this.setState({description:event.target.value});
  }

  handleLatitudeChange(event){
    this.setState({latitude:event.target.value});
  }

  handleLongitudeChange(event){
    this.setState({longitude:event.target.value});
  }

  render(){
    return (
      <div>
        <Form onSubmit={this.handleSubmit}>
          <Form.Group>
            <Form.Label>Name</Form.Label>
            <Form.Control placeholder="Enter Name of Event" onChange={this.handleNameChange}/>
          </Form.Group>
          <Form.Group>
            <Form.Label>Latitude</Form.Label>
            <Form.Control placeholder="Enter Latitude" onChange={this.handleLatitudeChange}/>
          </Form.Group>
          <Form.Group>
            <Form.Label>Longitude</Form.Label>
            <Form.Control placeholder="Enter Longitude" onChange={this.handleLongitudeChange}/>
          </Form.Group>
          <Form.Group>
            <Form.Label>Description</Form.Label>
            <Form.Control placeholder="Enter Description" onChange={this.handleDescChange}/>
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </div>
    )
  }
}

export default EventPage;
