import React, {Component} from 'react';
import GoogleMapReact from 'google-map-react';
import {Container,
        Row,
        Col,
        ListGroup} from 'react-bootstrap/';
import './css/MapPage.css';

class EventMarkerSmall extends Component {
  constructor(props){
    super(props);
  }

  render(){
    return <h1> {this.props.event.name} </h1>
  }
}

class SimpleMap extends Component {
  static defaultProps = {
    center: {
      lat: 58.95,
      lng: 30.33
    },
    zoom: 11
  };

  constructor(props){
    super(props);
    this.setState({lat:props.center.lat, lng:props.center.lng, events:props.events});
  }

  render() {
    return (
      //always set container height explicitly, required by google maps
      <div style={{height: '65vh', width: '100%'}}>
        <GoogleMapReact
          //we need to get a google maps api key to use full functionality
          //bootstrapURLKeys={{key:/*your key here*/}}
          defaultCenter={this.props.center}
          defaultZoom={this.props.zoom}
        >

        </GoogleMapReact>
      </div>
    );
  }
}

class MapPage extends Component {
  static defaultProps = {
    latitude: 0.0,
    longitude: 0.0,
    events:[]
  };

  constructor(props){
    super(props)
    this.state = {latitude:props.latitude,
                  longitude:props.longitude,
                  events:props.events}
  }

  render(){
    var event_list = []

    for(let i = 0; i < this.state.events.length; i++){
      event_list.push(<ListGroup.Item>{this.state.events[0].name}</ListGroup.Item>)
    }

    return(
      <div>
        <Container className='centered'>
          <Row>
            <Col xs={6}>
              <ListGroup>
                {event_list}
              </ListGroup>
            </Col>
            <Col xs={6}>
              <SimpleMap />
            </Col>
          </Row>
        </Container>
      </div>

    )

  }
}

export default MapPage;
